    })){
      this.saving = false;
      return alert('Pattern must have more than one color!');
    }

    this.http.fetch('/patterns', {
      method: 'post',
      body: json({
        name: this.name,
        description: '',
        width: this.width,
        height: this.height,
        align: this.align,
        pattern: this.pattern
      })
    })
    .then(response => response.json())
    .then(response => {
      $('#save-button').prop('disabled', false);
      $('#save-button').text('Save');
      this.saving = false;
      this.router.navigateToRoute('pattern', {id: response._id});
    })
    .catch(error => {
      alert('Error: ' + error);
      this.saving = false;
    });
  }

